(function ($) {
  var app = {
    init: function init() {
      $('.product-select-current').on('click', function () {
        $('.product-select-dropdown').toggleClass('active');
      });
      $('.product-select-item').on('click', app.handleProductSelect); //hide dropdown when click outside

      $(document).on('click', function (e) {
        if (!$(e.target).closest('.product-select').length) {
          $('.product-select-dropdown').removeClass('active');
        }

        e.stopPropagation();
      });
      $('.mp-submit').on('click', app.handleFormSubmit);
    },
    handleFormSubmit: function handleFormSubmit(e) {
      var $form = $(this).closest('form');
      var $input = $form.find('input[name="mp_source_id"]');
      var sourceId = $input.val();
      var email = $form.find('input[name="mp_email"]').val();

      if (!sourceId) {
        $('.product-error').addClass('active');
        e.preventDefault();
      } else {
        $('.product-error').removeClass('active');
      }

      var isEmailValid = app.validateEmail(email);

      if (!email || !isEmailValid) {
        $('.email-error').addClass('active');
        e.preventDefault();
      } else {
        $('.email-error').removeClass('active');
      }
    },
    handleProductSelect: function handleProductSelect() {
      var $this = $(this);
      var $current = $('.current-selection');
      var $dropdown = $('.product-select-dropdown');
      $current.html($this.html());
      $dropdown.removeClass('active');
      $('.product-select-item').removeClass('active');
      $this.addClass('active');
      var id = $this.data('id');
      $('input[name="mp_source_id"]').val(id);
    },
    validateEmail: function validateEmail(email) {
      var re = /\S+@\S+\.\S+/;
      return re.test(email);
    }
  };
  $(document).on('ready', app.init);
})(jQuery);
